#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Igor\crm-supabase-functions\node_modules\.pnpm\supabase@1.106.1\node_modules\supabase\bin\node_modules;C:\Igor\crm-supabase-functions\node_modules\.pnpm\supabase@1.106.1\node_modules\supabase\node_modules;C:\Igor\crm-supabase-functions\node_modules\.pnpm\supabase@1.106.1\node_modules;C:\Igor\crm-supabase-functions\node_modules\.pnpm\node_modules"
