  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Igor/crm-supabase-functions/node_modules/.pnpm/supabase@1.106.1/node_modules/supabase/bin/node_modules:/mnt/c/Igor/crm-supabase-functions/node_modules/.pnpm/supabase@1.106.1/node_modules/supabase/node_modules:/mnt/c/Igor/crm-supabase-functions/node_modules/.pnpm/supabase@1.106.1/node_modules:/mnt/c/Igor/crm-supabase-functions/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

# Support pipeline input
if ($MyInvocation.ExpectingInput) {
